<script type="text/javascript">
    const markChange = document.getElementsByClassName("mark-change");

    function search(){
        let regExp = new RegExp("<?php echo  $searchValue ?>", "gi");
        // console.log(markChange.length);
        for(let i=0;i<=markChange.length;i++){
            markChange[i].innerHTML = (markChange[i].textContent)?.replace(regExp, "<mark>$&</mark>");
            // console.log(markChange[i].textContent)

        }
        
    }
    search();
</script>
